import React, { useState } from "react";
import "./App.css";
import { Container, Form, Button, Table, Alert, Spinner } from "react-bootstrap";
import FHIR from "fhirclient";

const fhirClient = new FHIR.Client({ baseUrl: "https://fhir.epic.com/interconnect-fhir-oauth" });

function App() {
  const [searchTerm, setSearchTerm] = useState("");
  const [patients, setPatients] = useState([]);
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [documentReferences, setDocumentReferences] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [logMessages, setLogMessages] = useState([]);

  const log = (message, category = "Info") => {
    const timestamp = new Date().toISOString();
    setLogMessages((prev) => [...prev, `[${timestamp}] [${category}] ${message}`]);
  };

  const searchPatients = () => {
    if (!searchTerm) {
      log("Search term is empty.", "Warning");
      return;
    }

    setIsLoading(true);

    fhirClient
      .request(`Patient?name=${searchTerm}`)
      .then((response) => {
        const entries = response.entry || [];
        setPatients(entries.map((entry) => entry.resource));
        log(`Found ${entries.length} patients matching the search term.`);
      })
      .catch((error) => {
        log(`Error searching patients: ${error.message}`, "Error");
      })
      .finally(() => setIsLoading(false));
  };

  const selectPatient = (patient) => {
    setSelectedPatient(patient);
    setDocumentReferences([]);
    log(`Selected patient: ${patient.id}`);
  };

  const fetchDocumentReferences = () => {
    if (!selectedPatient) {
      log("No patient selected.", "Warning");
      return;
    }

    setIsLoading(true);

    fhirClient
      .request(`DocumentReference?patient=${selectedPatient.id}`)
      .then((response) => {
        const entries = response.entry || [];
        setDocumentReferences(entries.map((entry) => entry.resource));
        log(`Fetched ${entries.length} document references for the patient.`);
      })
      .catch((error) => {
        log(`Error fetching document references: ${error.message}`, "Error");
      })
      .finally(() => setIsLoading(false));
  };

  return (
    <Container className="py-4">
      <h1 className="text-center mb-4">Patient Management App</h1>

      <Alert variant="info">
        Search for patients, view their details, and edit their data.
      </Alert>

      <div className="mb-4">
        <h4>Debug Log</h4>
        <div style={{ maxHeight: "200px", overflowY: "auto", background: "#f8f9fa", padding: "10px", borderRadius: "8px" }}>
          {logMessages.map((msg, idx) => (
            <p key={idx} className="text-muted small mb-1">
              {msg}
            </p>
          ))}
        </div>
      </div>

      <Form className="mb-4">
        <Form.Group>
          <Form.Label>Search Patients</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter patient name or ID"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </Form.Group>
        <Button variant="primary" onClick={searchPatients} disabled={isLoading}>
          {isLoading ? "Searching..." : "Search"}
        </Button>
      </Form>

      {patients.length > 0 && (
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Gender</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {patients.map((patient) => (
              <tr key={patient.id}>
                <td>{patient.id}</td>
                <td>{patient.name?.[0]?.given?.join(" ")} {patient.name?.[0]?.family}</td>
                <td>{patient.gender}</td>
                <td>
                  <Button size="sm" variant="info" onClick={() => selectPatient(patient)}>
                    View Details
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}

      {selectedPatient && (
        <>
          <h4>Patient Details</h4>
          <p>
            <strong>ID:</strong> {selectedPatient.id}
          </p>
          <p>
            <strong>Name:</strong> {selectedPatient.name?.[0]?.given?.join(" ")} {selectedPatient.name?.[0]?.family}
          </p>
          <p>
            <strong>Gender:</strong> {selectedPatient.gender}
          </p>

          <Button variant="primary" className="mb-3" onClick={fetchDocumentReferences} disabled={isLoading}>
            {isLoading ? "Fetching..." : "Fetch Document References"}
          </Button>

          {documentReferences.length > 0 && (
            <Table striped bordered hover>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Type</th>
                  <th>Date</th>
                </tr>
              </thead>
              <tbody>
                {documentReferences.map((doc) => (
                  <tr key={doc.id}>
                    <td>{doc.id}</td>
                    <td>{doc.type?.text || "Unknown"}</td>
                    <td>{doc.date || "No date available"}</td>
                  </tr>
                ))}
              </tbody>
            </Table>
          )}
        </>
      )}
    </Container>
  );
}

export default App;
