import React, { useState } from "react";
import { Container, Form, Button, Table, Alert } from "react-bootstrap";
import axios from "axios";

const apiClient = axios.create({
  baseURL: "https://fhir.epic.com/interconnect-fhir-oauth",
  headers: {
    "Content-Type": "application/fhir+json",
  },
});

function App() {
  const [searchTerm, setSearchTerm] = useState("");
  const [patients, setPatients] = useState([]);
  const [logMessages, setLogMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const log = (message, category = "Info") => {
    const timestamp = new Date().toISOString();
    setLogMessages((prev) => [...prev, `[${timestamp}] [${category}] ${message}`]);
  };

  const searchPatients = async () => {
    if (!searchTerm) {
      log("Search term is empty.", "Warning");
      return;
    }

    setIsLoading(true);
    log(`Searching for patients with name: ${searchTerm}`);

    try {
      const response = await apiClient.get(`/Patient?name=${searchTerm}`);
      console.log("Patient search response:", response.data);
      const entries = response.data.entry || [];
      setPatients(entries.map((entry) => entry.resource));
      log(`Found ${entries.length} patients.`);
    } catch (error) {
      console.error("Error fetching patients:", error);
      log(`Error searching patients: ${error.message}`, "Error");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <Container>
      <h1 className="text-center mt-4">Patient Search App</h1>
      <Alert variant="info">Search for patients using their name or ID.</Alert>

      <div style={{ maxHeight: "200px", overflowY: "auto", background: "#f8f9fa", padding: "10px", marginBottom: "20px" }}>
        <h4>Logs:</h4>
        {logMessages.map((msg, idx) => (
          <p key={idx}>{msg}</p>
        ))}
      </div>

      <Form className="mb-4">
        <Form.Group>
          <Form.Label>Patient Name</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter patient name"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </Form.Group>
        <Button variant="primary" onClick={searchPatients} disabled={isLoading}>
          {isLoading ? "Searching..." : "Search"}
        </Button>
      </Form>

      {patients.length > 0 && (
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Gender</th>
            </tr>
          </thead>
          <tbody>
            {patients.map((patient) => (
              <tr key={patient.id}>
                <td>{patient.id}</td>
                <td>{patient.name?.[0]?.given?.join(" ")} {patient.name?.[0]?.family}</td>
                <td>{patient.gender}</td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </Container>
  );
}

export default App;
