import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function IntroScreen({ client }) {
  const [search, setSearch] = useState("");
  const [results, setResults] = useState([]);
  const navigate = useNavigate();

  const handleSearch = () => {
    client.request(`Patient?name=${search}`).then((data) => {
      setResults(data.entry || []);
    });
  };

  return (
    <div className="intro-screen">
      <h1>Search for a Patient</h1>
      <input
        type="text"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        placeholder="Enter patient name"
      />
      <button onClick={handleSearch}>Search</button>
      <ul>
        {results.map((entry) => (
          <li key={entry.resource.id}>
            <button onClick={() => navigate(`/patient/${entry.resource.id}`)}>
              {entry.resource.name?.[0]?.given?.join(" ")} {entry.resource.name?.[0]?.family}
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
