import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { Card, Button, Container, Row, Col } from "react-bootstrap";

export default function PatientList({ client }) {
  const [patients, setPatients] = useState([]);
  const navigate = useNavigate();

  // Fetch patients when the component loads
  useEffect(() => {
    client.request("Patient").then((data) => {
      const patientEntries = data.entry || [];
      setPatients(patientEntries.map((entry) => entry.resource));
    });
  }, [client]);

  return (
    <Container>
      <h1 className="text-center mt-4">Patient List</h1>
      <Row>
        {patients.map((patient) => (
          <Col md={6} lg={4} key={patient.id} className="mb-4">
            <Card>
              <Card.Body>
                <Card.Title>
                  {`${patient.name?.[0]?.given?.join(" ")} ${
                    patient.name?.[0]?.family
                  }`}
                </Card.Title>
                <Card.Text>
                  <strong>Gender:</strong> {patient.gender || "N/A"} <br />
                  <strong>Birthdate:</strong> {patient.birthDate || "N/A"} <br />
                </Card.Text>
                <Button
                  variant="primary"
                  onClick={() => navigate(`/patient/${patient.id}`)}
                >
                  View Details
                </Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
      {patients.length === 0 && (
        <p className="text-center">No patients found.</p>
      )}
    </Container>
  );
}
