import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Card, Button, Form, Container, Row, Col, InputGroup } from "react-bootstrap";

export default function PatientList({ client }) {
  const [patients, setPatients] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const navigate = useNavigate();

  // Fetch patient data
  useEffect(() => {
    const fetchPatientsForDoctor = async () => {
      try {
        // Fetch patients assigned to the logged-in doctor
        const response = await client.request(`Patient?general-practitioner=Practitioner/${doctorId}`);
        const patientEntries = response.entry || [];
        setPatients(patientEntries.map((entry) => entry.resource));
      } catch (error) {
        console.error("Error fetching patients for doctor:", error);
      }
    };

    if (doctorId) {
      fetchPatientsForDoctor();
    }
  }, [client, doctorId]);


  // Filter patients based on search term
  const filteredPatients = patients.filter((patient) => {
    const name = `${patient.name?.[0]?.given?.join(" ")} ${patient.name?.[0]?.family}`.toLowerCase();
    return name.includes(searchTerm.toLowerCase());
  });

  return (
    <Container>
      <h1 className="text-center mt-4">Patient List</h1>

      {/* Search Bar */}
      <Form className="mb-4">
        <InputGroup>
          <Form.Control
            type="text"
            placeholder="Search by name..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </InputGroup>
      </Form>

      {/* Patient Cards */}
      <Row>
        {filteredPatients.map((patient) => (
          <Col md={6} lg={4} key={patient.id} className="mb-4">
            <Card>
              <Card.Body>
                <Card.Title>
                  {`${patient.name?.[0]?.given?.join(" ")} ${patient.name?.[0]?.family}`}
                </Card.Title>
                <Card.Text>
                  <strong>Gender:</strong> {patient.gender || "N/A"} <br />
                  <strong>Birthdate:</strong> {patient.birthDate || "N/A"} <br />
                  <strong>ID:</strong> {patient.id}
                </Card.Text>
                <Button variant="primary" onClick={() => navigate(`/patient/${patient.id}`)}>
                  View Details
                </Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>

      {/* No Patients Found */}
      {filteredPatients.length === 0 && <p className="text-center">No patients found.</p>}
    </Container>
  );
}
