import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Card, Button, Container, Table, Form } from "react-bootstrap";

export default function PatientDetails({ client }) {
  const { id } = useParams();
  const [patient, setPatient] = useState(null);
  const [encounters, setEncounters] = useState([]);
  const [notes, setNotes] = useState([]);
  const [conditions, setConditions] = useState([]);
  const [newRows, setNewRows] = useState([]); // Manage newly added rows

  useEffect(() => {
    client.request(`Patient/${id}`).then((data) => setPatient(data));
    client.request(`Encounter?patient=${id}`).then((data) => {
      setEncounters(data.entry?.map((entry) => entry.resource) || []);
    });
    client.request(`DocumentReference?patient=${id}`).then((data) => {
      setNotes(data.entry?.map((entry) => entry.resource) || []);
    });
    client.request(`Condition?patient=${id}`).then((data) => {
      setConditions(data.entry?.map((entry) => entry.resource) || []);
    });
  }, [client, id]);

  const visitData = [
    ...encounters.map((encounter) => {
      const encounterId = encounter.id;
      const visitNotes = notes.filter(
        (note) => note.context?.reference === `Encounter/${encounterId}`
      );
      const visitConditions = conditions.filter(
        (condition) => condition.encounter?.reference === `Encounter/${encounterId}`
      );
      return {
        id: encounterId,
        date: encounter.period?.start || "",
        note: visitNotes[0]?.content?.[0]?.attachment?.data || "",
        icdCode: visitConditions[0]?.code?.coding?.[0]?.code || "",
        condition: visitConditions[0]?.code?.coding?.[0]?.display || "",
      };
    }),
    ...newRows,
  ];

  const handleEdit = (rowId, field, value) => {
    setNewRows((prev) =>
      prev.map((row) =>
        row.id === rowId
          ? {
              ...row,
              [field]: value,
            }
          : row
      )
    );
  };

  const addRow = () => {
    const newRow = {
      id: `new-${Date.now()}`,
      date: new Date().toISOString(),
      note: "",
      condition: "",
      icdCode: "",
    };
    setNewRows((prev) => [...prev, newRow]);
  };

  const saveChanges = async () => {
    try {
      console.log("Starting save process...");

      // Save existing conditions and notes
      const conditionPromises = conditions.map((condition) =>
        client.update(condition).catch((error) => {
          console.error("Error updating condition:", condition, error);
          throw error;
        })
      );

      const notePromises = notes.map((note) =>
        client.update(note).catch((error) => {
          console.error("Error updating note:", note, error);
          throw error;
        })
      );

      // Save new rows as FHIR resources
      for (const row of newRows) {
        console.log("Saving new row:", row);

        if (!row.date || !row.note || !row.condition || !row.icdCode) {
          console.warn("Skipping incomplete row:", row);
          continue;
        }

        const encounter = {
          resourceType: "Encounter",
          subject: { reference: `Patient/${id}` },
          period: { start: row.date },
        };
        const newEncounter = await client.create(encounter);

        console.log("New Encounter created:", newEncounter);

        const condition = {
          resourceType: "Condition",
          subject: { reference: `Patient/${id}` },
          encounter: { reference: `Encounter/${newEncounter.id}` },
          code: {
            coding: [
              {
                code: row.icdCode,
                display: row.condition,
              },
            ],
          },
        };
        await client.create(condition);

        console.log("New Condition created for Encounter:", newEncounter.id);

        const note = {
          resourceType: "DocumentReference",
          subject: { reference: `Patient/${id}` },
          context: { encounter: { reference: `Encounter/${newEncounter.id}` } },
          content: [
            {
              attachment: {
                contentType: "text/plain",
                data: btoa(row.note), // Encode note in Base64
              },
            },
          ],
        };
        await client.create(note);

        console.log("New Note created for Encounter:", newEncounter.id);
      }

      // Wait for all saves to complete
      await Promise.all([...conditionPromises, ...notePromises]);

      console.log("All changes saved successfully!");
      alert("Changes saved successfully!");

      setNewRows([]); // Clear new rows after saving

      // Refresh data
      const updatedEncounters = await client.request(`Encounter?patient=${id}`);
      setEncounters(updatedEncounters.entry?.map((entry) => entry.resource) || []);

      const updatedNotes = await client.request(`DocumentReference?patient=${id}`);
      setNotes(updatedNotes.entry?.map((entry) => entry.resource) || []);

      const updatedConditions = await client.request(`Condition?patient=${id}`);
      setConditions(updatedConditions.entry?.map((entry) => entry.resource) || []);
    } catch (error) {
      console.error("Error saving changes:", error);
      alert("Error saving changes.");
    }
  };

  return (
    <Container>
      {patient ? (
        <>
          <h1 className="text-center mt-4">Patient Details</h1>

          {/* Patient Basic Details */}
          <Card className="mb-4">
            <Card.Header as="h5">Basic Information</Card.Header>
            <Card.Body>
              <p>
                <strong>Full Name:</strong>{" "}
                {`${patient.name?.[0]?.given?.join(" ")} ${patient.name?.[0]?.family}`}
              </p>
              <p>
                <strong>Gender:</strong> {patient.gender || "N/A"}
              </p>
              <p>
                <strong>Birthdate:</strong> {patient.birthDate || "N/A"}
              </p>
              <p>
                <strong>Address:</strong>{" "}
                {patient.address?.[0]?.line?.join(", ") || "N/A"}, {patient.address?.[0]?.city || "N/A"}
              </p>
            </Card.Body>
          </Card>

          {/* Visit Details */}
          <Card className="mb-4">
            <Card.Header as="h5">Visit Details</Card.Header>
            <Card.Body>
              <Table striped bordered hover>
                <thead>
                  <tr>
                    <th>Date of Visit</th>
                    <th>Note</th>
                    <th>Condition</th>
                    <th>ICD Code</th>
                  </tr>
                </thead>
                <tbody>
                  {visitData.map((visit) => (
                    <tr key={visit.id}>
                      <td>
                        <Form.Control
                          type="text"
                          value={visit.date || ""}
                          readOnly
                        />
                      </td>
                      <td>
                        <Form.Control
                          as="textarea"
                          rows={2}
                          value={visit.note || ""}
                          onChange={(e) =>
                            handleEdit(visit.id, "note", e.target.value || "")
                          }
                        />
                      </td>
                      <td>
                        <Form.Control
                          type="text"
                          value={visit.condition || ""}
                          onChange={(e) =>
                            handleEdit(visit.id, "condition", e.target.value || "")
                          }
                        />
                      </td>
                      <td>
                        <Form.Control
                          type="text"
                          value={visit.icdCode || ""}
                          onChange={(e) =>
                            handleEdit(visit.id, "icdCode", e.target.value || "")
                          }
                        />
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
              <Button variant="success" onClick={addRow} className="mt-3">
                Add Row
              </Button>
            </Card.Body>
          </Card>

          <div className="text-center">
            <Button variant="primary" onClick={saveChanges}>
              Save All Changes
            </Button>
          </div>
        </>
      ) : (
        <p>Loading patient data...</p>
      )}
    </Container>
  );
}
