import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Button, Container, Table, Form } from "react-bootstrap";
import "./PatientDetails.css";

export default function PatientDetails({ client }) {
  const { id } = useParams();
  const [patient, setPatient] = useState(null);
  const [rows, setRows] = useState([]); // Unified state for all rows (new and existing)

  useEffect(() => {
    const fetchData = async () => {
      const patientData = await client.request(`Patient/${id}`);
      setPatient(patientData);

      const encounters = await client.request(`Encounter?patient=${id}`);
      const encounterResources = encounters.entry?.map((entry) => entry.resource) || [];

      const notes = await client.request(`DocumentReference?patient=${id}`);
      const noteResources = notes.entry?.map((entry) => entry.resource) || [];

      const conditions = await client.request(`Condition?patient=${id}`);
      const conditionResources =
        conditions.entry?.map((entry) => entry.resource) || [];

      const mergedRows = encounterResources.map((encounter) => {
        const encounterId = encounter.id;
        const encounterNotes = noteResources.filter(
          (note) => note.context?.encounter?.reference === `Encounter/${encounterId}`
        );
        const encounterConditions = conditionResources.filter(
          (condition) => condition.encounter?.reference === `Encounter/${encounterId}`
        );
        return {
          id: encounterId,
          date: encounter.period?.start || new Date().toISOString(),
          note: encounterNotes[0]?.content?.[0]?.attachment?.data
            ? atob(encounterNotes[0]?.content?.[0]?.attachment?.data)
            : "",
          noteId: encounterNotes[0]?.id || null,
          icdCode: encounterConditions[0]?.code?.coding?.[0]?.code || "",
          condition: encounterConditions[0]?.code?.coding?.[0]?.display || "",
          conditionId: encounterConditions[0]?.id || null,
          isNew: false,
        };
      });

      setRows(mergedRows);
    };

    fetchData();
  }, [client, id]);

  const handleEdit = (rowId, field, value) => {
    setRows((prev) =>
      prev.map((row) =>
        row.id === rowId
          ? {
              ...row,
              [field]: value,
            }
          : row
      )
    );
  };

  const addRow = () => {
    const newRow = {
      id: `new-${Date.now()}`,
      date: new Date().toISOString(),
      note: "",
      condition: "",
      icdCode: "",
      isNew: true,
    };
    setRows((prev) => [...prev, newRow]);
  };

  const saveChanges = async () => {
    try {
      const savePromises = rows.map(async (row) => {
        if (row.isNew) {
          // Save new rows
          const encounter = {
            resourceType: "Encounter",
            subject: { reference: `Patient/${id}` },
            period: { start: row.date },
          };
          const newEncounter = await client.create(encounter);

          const condition = {
            resourceType: "Condition",
            subject: { reference: `Patient/${id}` },
            encounter: { reference: `Encounter/${newEncounter.id}` },
            code: {
              coding: [
                {
                  code: row.icdCode,
                  display: row.condition,
                },
              ],
            },
          };
          await client.create(condition);

          const note = {
            resourceType: "DocumentReference",
            subject: { reference: `Patient/${id}` },
            context: { encounter: { reference: `Encounter/${newEncounter.id}` } },
            content: [
              {
                attachment: {
                  contentType: "text/plain",
                  data: btoa(row.note),
                },
              },
            ],
          };
          await client.create(note);
        } else {
          // Update existing rows
          if (row.conditionId) {
            const condition = {
              resourceType: "Condition",
              id: row.conditionId,
              subject: { reference: `Patient/${id}` },
              code: {
                coding: [
                  {
                    code: row.icdCode,
                    display: row.condition,
                  },
                ],
              },
            };
            await client.update(condition);
          }

          if (row.noteId) {
            const note = {
              resourceType: "DocumentReference",
              id: row.noteId,
              subject: { reference: `Patient/${id}` },
              content: [
                {
                  attachment: {
                    contentType: "text/plain",
                    data: btoa(row.note),
                  },
                },
              ],
            };
            await client.update(note);
          }
        }
      });

      await Promise.all(savePromises);

      alert("Changes saved successfully!");
    } catch (error) {
      console.error("Error saving changes:", error);
      alert("Error saving changes.");
    }
  };

  const formatDate = (date) => {
    if (!date) return "N/A";
    return new Intl.DateTimeFormat("en-US", { dateStyle: "long" }).format(
      new Date(date)
    );
  };

  return (
    <Container className="patient-details-container">
      {patient ? (
        <>
          <h1 className="modern-title">Patient Details</h1>

          {/* Patient Basic Information */}
          <div className="custom-card mb-4">
            <div className="custom-card-header">Basic Information</div>
            <div className="custom-card-body">
              <p>
                <strong>Full Name:</strong>{" "}
                {`${patient.name?.[0]?.given?.join(" ")} ${patient.name?.[0]?.family}`}
              </p>
              <p>
                <strong>Gender:</strong> {patient.gender || "N/A"}
              </p>
              <p>
                <strong>Birthdate:</strong> {formatDate(patient.birthDate)}
              </p>
            </div>
          </div>

          {/* Visit Details */}
          <div className="custom-card mb-4">
            <div className="custom-card-header">Visit Details</div>
            <div className="custom-card-body">
              <Table className="modern-table" responsive>
                <thead>
                  <tr>
                    <th>Date of Visit</th>
                    <th>Note</th>
                    <th>Condition</th>
                    <th>ICD Code</th>
                  </tr>
                </thead>
                <tbody>
                  {rows.map((row) => (
                    <tr key={row.id}>
                      <td>{formatDate(row.date)}</td>
                      <td>
                        <Form.Control
                          as="textarea"
                          rows={2}
                          className="modern-input"
                          value={row.note || ""}
                          onChange={(e) =>
                            handleEdit(row.id, "note", e.target.value || "")
                          }
                        />
                      </td>
                      <td>
                        <Form.Control
                          type="text"
                          className="modern-input"
                          value={row.condition || ""}
                          onChange={(e) =>
                            handleEdit(row.id, "condition", e.target.value || "")
                          }
                        />
                      </td>
                      <td>
                        <Form.Control
                          type="text"
                          className="modern-input"
                          value={row.icdCode || ""}
                          onChange={(e) =>
                            handleEdit(row.id, "icdCode", e.target.value || "")
                          }
                        />
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
              <Button variant="success" className="modern-button mt-3" onClick={addRow}>
                Add Row
              </Button>
            </div>
          </div>

          <div className="text-center">
            <Button variant="primary" className="modern-button" onClick={saveChanges}>
              Save All Changes
            </Button>
          </div>
        </>
      ) : (
        <p>Loading patient data...</p>
      )}
    </Container>
  );
}
