//index.js
import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import App from './App';
import FHIR from 'fhirclient';

const rootElement = document.getElementById('root');

const smartLaunch = () => {
  // Authorize application
  FHIR.oauth2
  FHIR.oauth2.init({
    clientId: '163c83cd-ec2e-45d1-881e-28177fcc8089',
    scope: 'patient/DocumentReference.read patient/DocumentReference.write patient/Observation.read patient/Observation.write openid profile',
    redirectUri: 'http://localhost:3000',
    iss: 'https://fhir.epic.com/interconnect-fhir-oauth' // Replace with the actual FHIR base URL
})
.then(client => {
    ReactDOM.render(<App client={client} />, rootElement);
})
.catch(error => {
    console.error('FHIR initialization failed:', error);
});

};

smartLaunch();
