import React, { useEffect, useState } from "react";

export default function PatientDetails({ client }) {
  const [patient, setPatient] = useState(null);
  const patientId = window.location.pathname.split("/").pop();

  useEffect(() => {
    client.patient.read({ id: patientId }).then(setPatient);
  }, [client, patientId]);

  const handleEdit = (field, value) => {
    setPatient((prev) => ({
      ...prev,
      [field]: value,
    }));
  };

  const saveChanges = () => {
    client.patient.update(patient).then(() => {
      alert("Changes saved!");
    });
  };

  return (
    <div className="patient-details">
      {patient ? (
        <>
          <h1>{`${patient.name?.[0]?.given?.join(" ")} ${patient.name?.[0]?.family}`}</h1>
          <input
            type="text"
            value={patient.address?.[0]?.line?.[0] || ""}
            onChange={(e) => handleEdit("address", [{ line: [e.target.value] }])}
          />
          <button onClick={saveChanges}>Save</button>
        </>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}
