//index.js
import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import App from './App';
import FHIR from 'fhirclient';

const rootElement = document.getElementById('root');

const smartLaunch = () => {
  // Authorize application
  FHIR.oauth2
    .init({
      //Adjust the client depending on which EHR you use:
      //No specific client needed for SMART on FHIR Launcher - https://launch.smarthealthit.org/
      //Use non production client id in Cerner and Epic Sandboxes. This code is designed for provider facing apps

      //Adjust scope depending on what information about the patient you are trying to access, and what operations you want to perform.

      //clientId: 'cb74b87a-fa1d-4d92-af7c-d69ad4ead710',
      clientId: '3e610e34-4728-487f-affc-2111b103b7d8',
      scope: 'patient/DocumentReference.read patient/DocumentReference.write patient/Observation.read patient/Observation.write openid profile',

      //clientId: '404e92be-0de8-4b30-87ab-6e01abd1993e',
      //scope: 'patient/Procedure.read patient/Procedure.write patient/Observation.read patient/Observation.write patient/Condition.read patient/Condition.write patient/Patient.read patient/Patient.write openid profile',
     
      //Specify this redirect URI as the launching link. Enter 'yarn start' in the terminal, and in the launcher enter the localhost:3000 link.
      redirectUri: 'http://localhost:3000',
    })
    .then(client => {
      ReactDOM.render(<App client={client} />, rootElement);
    });
};

smartLaunch();
