import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Card, Button, Form, Container, Row, Col } from "react-bootstrap";

export default function PatientDetails({ client }) {
  const { id } = useParams();
  const [patient, setPatient] = useState(null);
  const [notes, setNotes] = useState([]);
  const [conditions, setConditions] = useState([]);

  useEffect(() => {
    client.request(`Patient/${id}`).then((data) => setPatient(data));
    client.request(`DocumentReference?patient=${id}`).then((data) => {
      setNotes(data.entry?.map((entry) => entry.resource) || []);
    });
    client.request(`Condition?patient=${id}`).then((data) => {
      setConditions(data.entry?.map((entry) => entry.resource) || []);
    });
  }, [client, id]);

  const handlePatientEdit = (field, value) => {
    setPatient((prev) => ({
      ...prev,
      [field]: value,
    }));
  };

  const saveChanges = () => {
    // Update the patient details
    client
      .update(patient)
      .then(() => {
        // Update the conditions
        const conditionPromises = conditions.map((condition) =>
          client.update(condition)
        );

        // Update the notes
        const notePromises = notes.map((note) =>
          client.update(note)
        );

        // Wait for all updates to complete
        Promise.all([...conditionPromises, ...notePromises])
          .then(() => {
            alert("All changes saved successfully!");
          })
          .catch((error) => {
            console.error("Error saving conditions or notes:", error);
            alert("Error saving some changes.");
          });
      })
      .catch((error) => {
        console.error("Error saving patient details:", error);
        alert("Error saving patient details.");
      });
  };


  return (
    <Container>
      {patient ? (
        <>
          <h1 className="text-center mt-4">Patient Details</h1>

          {/* Basic Details */}
          <Card className="mb-4">
            <Card.Header as="h5">Basic Information</Card.Header>
            <Card.Body>
              <Form>
                <Row>
                  <Col md={6}>
                    <Form.Group>
                      <Form.Label>Full Name</Form.Label>
                      <Form.Control
                        type="text"
                        value={`${patient.name?.[0]?.given?.join(" ")} ${
                          patient.name?.[0]?.family
                        }`}
                        onChange={(e) =>
                          handlePatientEdit("name", [
                            {
                              ...patient.name?.[0],
                              given: [e.target.value.split(" ")[0]],
                              family: e.target.value.split(" ").slice(1).join(" "),
                            },
                          ])
                        }
                      />
                    </Form.Group>
                  </Col>
                  <Col md={3}>
                    <Form.Group>
                      <Form.Label>Gender</Form.Label>
                      <Form.Control
                        type="text"
                        value={patient.gender || ""}
                        onChange={(e) => handlePatientEdit("gender", e.target.value)}
                      />
                    </Form.Group>
                  </Col>
                  <Col md={3}>
                    <Form.Group>
                      <Form.Label>Birthdate</Form.Label>
                      <Form.Control
                        type="date"
                        value={patient.birthDate || ""}
                        onChange={(e) =>
                          handlePatientEdit("birthDate", e.target.value)
                        }
                      />
                    </Form.Group>
                  </Col>
                </Row>
              </Form>
            </Card.Body>
          </Card>

          {/* Notes Section */}
          <Card className="mb-4">
            <Card.Header as="h5">Notes</Card.Header>
            <Card.Body>
              {notes.map((note, index) => (
                <Form key={index} className="mb-3">
                  <Form.Group>
                    <Form.Label>Title</Form.Label>
                    <Form.Control
                      type="text"
                      value={note.type?.text || ""}
                      onChange={(e) =>
                        setNotes((prev) =>
                          prev.map((n, i) =>
                            i === index
                              ? { ...n, type: { ...n.type, text: e.target.value } }
                              : n
                          )
                        )
                      }
                    />
                  </Form.Group>
                  <Form.Group>
                    <Form.Label>Content</Form.Label>
                    <Form.Control
                      as="textarea"
                      rows={2}
                      value={note.content?.[0]?.attachment?.data || ""}
                      onChange={(e) =>
                        setNotes((prev) =>
                          prev.map((n, i) =>
                            i === index
                              ? {
                                  ...n,
                                  content: [
                                    {
                                      attachment: {
                                        ...n.content?.[0]?.attachment,
                                        data: e.target.value,
                                      },
                                    },
                                  ],
                                }
                              : n
                          )
                        )
                      }
                    />
                  </Form.Group>
                </Form>
              ))}
            </Card.Body>
          </Card>

          {/* ICD Codes Section */}
          <Card className="mb-4">
            <Card.Header as="h5">ICD Codes</Card.Header>
            <Card.Body>
              {conditions.map((condition, index) => (
                <Form key={index} className="mb-3">
                  <Row>
                    <Col md={8}>
                      <Form.Group>
                        <Form.Label>Condition</Form.Label>
                        <Form.Control
                          type="text"
                          value={condition.code?.coding?.[0]?.display || ""}
                          onChange={(e) =>
                            setConditions((prev) =>
                              prev.map((c, i) =>
                                i === index
                                  ? {
                                      ...c,
                                      code: {
                                        ...c.code,
                                        coding: [
                                          {
                                            ...c.code?.coding?.[0],
                                            display: e.target.value,
                                          },
                                        ],
                                      },
                                    }
                                  : c
                              )
                            )
                          }
                        />
                      </Form.Group>
                    </Col>
                    <Col md={4}>
                      <Form.Group>
                        <Form.Label>ICD Code</Form.Label>
                        <Form.Control
                          type="text"
                          value={condition.code?.coding?.[0]?.code || ""}
                          onChange={(e) =>
                            setConditions((prev) =>
                              prev.map((c, i) =>
                                i === index
                                  ? {
                                      ...c,
                                      code: {
                                        ...c.code,
                                        coding: [
                                          {
                                            ...c.code?.coding?.[0],
                                            code: e.target.value,
                                          },
                                        ],
                                      },
                                    }
                                  : c
                              )
                            )
                          }
                        />
                      </Form.Group>
                    </Col>
                  </Row>
                </Form>
              ))}
            </Card.Body>
          </Card>

          <div className="text-center">
            <Button variant="primary" onClick={saveChanges}>
              Save All Changes
            </Button>
          </div>
        </>
      ) : (
        <p>Loading patient data...</p>
      )}
    </Container>
  );
}
